installer-script.script:
function pm_volume_check() {
  if(!(my.target.systemVersion && /* >= */ system.compareVersions(my.target.systemVersion.ProductVersion, '10.6.0') >= 0)) {
    my.result.title = 'Failure';
    my.result.message = 'This version of Elm requires Mac OS X 10.6 or later.';
    my.result.type = 'Fatal';
    return false;
  }
  return true;
}
